#!/bin/bash# Script de build pour ZenLink# Usage: ./build_release.shset -eecho "ğŸ”— Building ZenLink Release..."# Clean previous buildsecho "ğŸ§¹ Cleaning previous builds..."xcodebuild clean -project ZenLink.xcodeproj -scheme ZenLink -configuration Release# Build Releaseecho "ğŸ—ï¸ Building Release..."xcodebuild -project ZenLink.xcodeproj -scheme ZenLink -configuration Release -derivedDataPath ./build_output# Find the built appAPP_PATH="./build_output/Build/Products/Release/ZenLink.app"if [ -d "$APP_PATH" ]; then    echo "âœ… Build successful!"    echo "ğŸ“± App location: $APP_PATH"        # Create release directory    RELEASE_DIR="./Release"    mkdir -p "$RELEASE_DIR"        # Copy app to release directory    cp -R "$APP_PATH" "$RELEASE_DIR/"        # Create ZIP for distribution    cd "$RELEASE_DIR"    zip -r "ZenLink-v1.0.0.zip" "ZenLink.app"    cd ..        echo "ğŸ“¦ Release package created: $RELEASE_DIR/ZenLink-v1.0.0.zip"    echo "ğŸ‰ Ready for distribution!"        # Show file info    echo ""    echo "ğŸ“Š Package info:"    ls -lh "$RELEASE_DIR/ZenLink-v1.0.0.zip"    else    echo "âŒ Build failed - app not found at $APP_PATH"    exit 1fi
