#!/bin/bash# Script de build pour ZenLink# Usage: ./build_release.shset -eecho "🔗 Building ZenLink Release..."# Clean previous buildsecho "🧹 Cleaning previous builds..."xcodebuild clean -project ZenLink.xcodeproj -scheme ZenLink -configuration Release# Build Releaseecho "🏗️ Building Release..."xcodebuild -project ZenLink.xcodeproj -scheme ZenLink -configuration Release -derivedDataPath ./build_output# Find the built appAPP_PATH="./build_output/Build/Products/Release/ZenLink.app"if [ -d "$APP_PATH" ]; then    echo "✅ Build successful!"    echo "📱 App location: $APP_PATH"        # Create release directory    RELEASE_DIR="./Release"    mkdir -p "$RELEASE_DIR"        # Copy app to release directory    cp -R "$APP_PATH" "$RELEASE_DIR/"        # Create ZIP for distribution    cd "$RELEASE_DIR"    zip -r "ZenLink-v1.0.0.zip" "ZenLink.app"    cd ..        echo "📦 Release package created: $RELEASE_DIR/ZenLink-v1.0.0.zip"    echo "🎉 Ready for distribution!"        # Show file info    echo ""    echo "📊 Package info:"    ls -lh "$RELEASE_DIR/ZenLink-v1.0.0.zip"    else    echo "❌ Build failed - app not found at $APP_PATH"    exit 1fi
